import { Image, Table, Typography } from "antd";
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import Loader from "../../../../Components/Loader/Loader";
import { useGetAuthenticatedUser } from "../../../../hooks/useGetAuthenticatedUser";
import { getCustomerOrders } from "../../../../services/customer-services/customer-track-order-service";

const {  Paragraph } = Typography;

const columns = [
  {
    title: "Order Number",
    dataIndex: "ordersId",
    key: "ordersId",
  },
  {
    title: "Status",
    dataIndex: "status",
    key: "status",
  },
  {
    title: "Date Of Creation",
    dataIndex: "dateOfCreation",
    key: "dateOfCreation",
  },
  {
    title: "Total Price",
    dataIndex: "totalPrice",
    key: "totalPrice",
  },
];

const TrackOrderTable = () => {
  const [isLoading, setisLoading] = useState({ state: "ok", message: "" });
  const [orders, setOrders] = useState([]);
  const dispatch = useDispatch();
  const user = useGetAuthenticatedUser();

  useEffect(() => {
    setisLoading({
      state: "loading",
      message: "",
    });
    dispatch(
      getCustomerOrders({
        token: user.token,
        hooks: { setisLoading, setOrders },
      })
    );
  }, [dispatch, setisLoading, setOrders, user]);

  return (
    <Table
      columns={columns}
      className="my-3"
      loading={{
        indicator: <Loader />,
        spinning: isLoading.state === "loading",
      }}
      expandable={{
        expandedRowRender: (record) => <EachOrders orders={record.orders} />,
        rowExpandable: (record) => record.orders.length > 0,
        expandRowByClick: true,
      }}
      rowKey={(record) => record.ordersId}
      dataSource={orders}
    />
  );
};

export default TrackOrderTable;

const EachOrders = ({ orders }) => {
  return orders.map((order, index) => {
    return <EachOrder order={order} key={index} />;
  });
};

const EachOrder = ({ order }) => {
  return (
    <section className="d-flex ">
      <Image
        width={70}
        height={70}
        src={order.product.productCoverUrl}
        fallback="data:image/png;base64,"
      />
      <div className="mx-5 d-flex flex-column justify-content-center my-2">
        <Paragraph className="mb-0" strong>
          Status
        </Paragraph>
        <Paragraph className="mb-0">{order.status}</Paragraph>
      </div>
      <div className="mx-5 d-flex flex-column justify-content-center my-2">
        <Paragraph className="mb-0" strong>
          Quantity
        </Paragraph>
        <Paragraph className="mb-0">{order.quantity}</Paragraph>
      </div>
      <div className="mx-5 d-flex flex-column justify-content-center align-items-center my-2">
        <Paragraph className="mb-1" strong>
          Supplier
        </Paragraph>
        <Image
        width={40}
        height={40}
        preview={false}
        src={order.supplier.supplierProfileUrl}
        fallback="data:image/png;base64,"
      />
      </div>
      <div className="mx-5 d-flex flex-column justify-content-center my-2">
        <Paragraph className="mb-0" strong>
          Supplier Contact #
        </Paragraph>
        <Paragraph className="mb-0">{order.supplier.supplierContactNo}</Paragraph>
      </div>
    </section>
  );
};
