import { Avatar, Button, Card, Grid, Image, Rate, Typography } from "antd";
import React from "react";
import { AiOutlineShoppingCart } from "react-icons/ai";
import {  useNavigate } from "react-router-dom";

const { useBreakpoint } = Grid;
const { Meta } = Card;
const { Text } = Typography;

function ProductCard({ product, handleAddToCart }) {
  const navigate = useNavigate();

  const { xs, lg, md } = useBreakpoint();

  const handleNavigateToProductPage = (event, product) => {
    event.preventDefault();

    if (!product) return;
    const { productId } = product;
    console.log(productId);
    console.log(typeof `/product/${productId}`.toString());
    navigate("/product/" + productId, { replace: true });
  };

  return (
    <Card
      className=""
      hoverable={true}
      style={{
        width: lg ? 180 : "100%",
        display: lg ? "block" : md ? "flex" : xs ? "block" : "",
        alignItems: "center",
        padding: lg ? "" : md ? "0 1.2rem" : xs ? "" : "",
        borderRadius: 7,
      }}
      bodyStyle={{
        padding: "24px 10px",
        width: "100%",
        display: "flex",
        flexDirection: "column",
      }}
      cover={
        <Image
          onClick={(event) => handleNavigateToProductPage(event, product)}
          loading="lazy"
          width={lg ? "100%" : md ? 200 : xs ? "100%" : "100%"}
          height={150}
          src={product.productCoverUrl}
          alt={product.productName}
          preview={false}
          style={{ objectFit: "cover" }}
          fallback="data:image/png;base64,"
        />
      }
    >
      <Meta
        avatar={<Avatar src={product.supplier.supplierProfileUrl}></Avatar>}
        title={product.supplier.brandName.slice(0, 7) + "..."}
        className="mb-2"
      />
      <Text className="my-1" strong>
        {product.productName}
      </Text>
      <Text className="w-100 my-1">
        Rs:{" "}
        {`${product.productPrice}
          |
          ${product.productWeight.slice(0, 6)}
          
          ${product.productWeight.length > 6 ? "..." : ""}
`}
      </Text>

      <Rate
        className="my-1"
        style={{ fontSize: "10px" }}
        disabled
        defaultValue={2}
      />
      <Button
        type="primary"
        block
        className="d-flex justify-content-center align-items-center mt-2 mb-0"
        size="middle"
        icon={<AiOutlineShoppingCart className="me-2" />}
        style={{ borderRadius: "7px" }}
        onClick={() => handleAddToCart(product)}
      >
        Add to Cart
      </Button>
    </Card>
  );
}

export default ProductCard;
