import { Button, Card, Grid, Image, Rate } from "antd";
import React from "react";
import { AiOutlineShoppingCart } from "react-icons/ai";

const { useBreakpoint } = Grid;
const { Meta } = Card;

function ProductCard({ product, handleAddToCart }) {
  const { xs, lg, md } = useBreakpoint();

  return (
    <Card
      className=""
      hoverable={true}
      style={{
        width: lg ? 180 : "100%",
        display: lg ? "block" : md ? "flex" : xs ? "block" : "",
        alignItems: "center",
        padding: lg ? "" : md ? "0 1.2rem" : xs ? "" : "",
        borderRadius: 7
      }}
      cover={
        <Image
          loading="lazy"
          width={lg ? "100%" : md ? 200 : xs ? "100%" : "100%"}
          height={150}
          src={product.productCoverUrl}
          alt={product.productName}
          preview={false}
          style={{ objectFit: "cover",}}
          fallback="data:image/png;base64,"
        />
      }
    >
      <Meta title={product.productName} description={`Rs: ${product.productPrice} | ${product.productWeight.slice(0,7)}`} />
      <Rate
        className="my-2"
        style={{ fontSize: "10px" }}
        disabled
        defaultValue={2}
      />
      <Button
        type="primary"
        block
        className="d-flex justify-content-center align-items-center mt-2 mb-0"
        size="middle"
        icon={<AiOutlineShoppingCart className="me-2" />}
        style={{ borderRadius: "7px" }}
        onClick={() => handleAddToCart(product)}
      >
        Add to Cart
      </Button>
    </Card>
  );
}

export default ProductCard;
